mapper.update:
update msg
set recv_del='삭제'
where msg_num=#{msg_num}

update msg
set send_del='삭제'
where msg_num=#{msg_num}

update msg
set chk='읽음'
where msg_num=#{msg_num}

mapper.select:
select msg_num, content, send_del, recv_del, date_format(regdate,'%Y-%m-%d %H:%i') regdate, send_clx, chk, sender, receiver
from msg
where msg_num=#{msg_num}

mapper.update:
update msg
set send_clx='0'
where msg_num=#{msg_num}
